item.solution:
int main(int argc, char* argv[])
{
  char str[100];
  int i;
  while(gets(str))
  {
     for(i=0;str[i]!='\0';i++)
     { 
		if(str[i]>='a'&&str[i]<='y')
		 str[i]+=1;
	    else if(str[i]=='z')
		 str[i]='a';
	 }
	 puts(str);
  }
	return 0;
}

#include <iostream>
#include <cstring>
using namespace std;
int main ()
{
    char c[1000];
    gets(c);
    int l = strlen (c);
    for (int i = 0;i < l; i++)
    {
        if ('a'<=c[i]&&c[i]<'z')
            c[i]++;
        if (c[i] == 'z')
            c[i] = 'a';
    }
    cout<<c<<endl;
    return 0;
}
