var  s1:string;
     i,len:longint;
begin
while not eof do
begin
 readln(s1);
 len:=length(s1);
 for i:=1 to len do
  case s1[i] of
  'A'..'Y':s1[i]:=chr(ord(s1[i])+33);
       'Z':s1[i]:='a';
  '0'..'9':s1[i]:=s1[i];
  'a'..'c':s1[i]:='2';
  'd'..'f':s1[i]:='3';
  'g'..'i':s1[i]:='4';
  'j'..'l':s1[i]:='5';
  'm'..'o':s1[i]:='6';
  'p'..'s':s1[i]:='7';
  't'..'v':s1[i]:='8';
  'w'..'z':s1[i]:='9';
 end;
 writeln(s1);
end;
end.
