#include<cstdio>
#include<cstdlib>
#include<cstring>
using namespace std;
const int maxn=200+5;
char str[maxn];
char a[maxn],b[maxn];

int main()
{
    while(gets(a))
    {
        if(strcmp(a,"ENDOFINPUT")==0)
         break;
        else if(strcmp(a,"START")==0)
        {
            gets(str);
            gets(b);
            int len=strlen(str);
            for(int i=0;i<len;i++)
            if(str[i]>='F'&&str[i]<='Z')
            str[i]=str[i]-5;
            else if(str[i]<='E'&&str[i]>='A')
            str[i]='Z'+(str[i]-'A')-4;
        }
        printf("%s\n",str);
    }
    return 0;
}

import java.util.Scanner;

public class Main {
	public static void main(String args[]) throws Exception {
		Scanner sc = new Scanner(System.in);
		String startFlag = "";
		String endFlag = "";
		int len = 0;
		char[] array;
		while (sc.hasNext()) {
			startFlag = sc.nextLine();
			if ("START".equals(startFlag)) {
				array = sc.nextLine().toCharArray();
				len = array.length;
				for (int i = 0; i < len; i++) {
					if (array[i]>='A' && array[i] <= 'E') {
						array[i] = (char) (array[i] + 'v' - 'a');
					}else if (array[i]>='F' && array[i] <= 'Z') {
						array[i] = (char) (array[i] - 5);
					}
					System.out.print(array[i]);
				}
				System.out.println();
				endFlag = sc.nextLine();
			} else if ("ENDOFINPUT".equals(startFlag)) {
				break;
			}
		}
	}
}
