item.solution:
var
  a1,a2,r10,a,t12,s16:longint;
begin
  readln(a);
  a1:=a;a2:=a;
  repeat
   r10:=r10+a mod 10;
   a:=a div 10;
  until a=0;
  repeat
   t12:=t12+a1 mod 12;
   a1:=a1 div 12;
  until a1=0;
  repeat
   s16:=s16+a2 mod 16;
   a2:=a2 div 16;
  until a2=0;
 if (r10=t12) and (r10=s16) and (t12=s16) then
   begin
   writeln(s16);
   writeln('Right');
   end
   else writeln('Wrong');
end.
